DROP TABLE IF EXISTS `t_news`;
CREATE TABLE `t_news` (
  `news_id` int(11) NOT NULL AUTO_INCREMENT,
  `news_title` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `news_content` text COLLATE utf8_bin,
  `lgt` double(11,8) DEFAULT NULL,
  `lat` double(11,8) DEFAULT NULL,
  `news_level` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `news_address_name` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `news_date` date DEFAULT NULL,
  `news_category` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`news_id`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

